DROP SEQUENCE IF EXISTS SEQ_TERMINAL_ID;
DROP TABLE IF EXISTS MOBILE_PHONE;

-- シーケンス作成
CREATE SEQUENCE IF NOT EXISTS SEQ_TERMINAL_ID INCREMENT BY 1 START 1;
-- テーブル作成
CREATE TABLE IF NOT EXISTS MOBILE_PHONE (
 TERMINAL_ID NUMBER(9,0) NOT NULL,
 TERMINAL_NAME VARCHAR2(256) NOT NULL,
 SALES_DATE DATE NOT NULL,
 VENDOR_NAME VARCHAR2(256),
 CARRIER_TYPE CHAR(1) DEFAULT '0' NOT NULL,
 FLASH_LEVEL NUMBER(4,2),
 GIF_FLAG CHAR(1),
 VERSION NUMBER(2) DEFAULT 0 NOT NULL,
 DEL_FLAG VARCHAR2(1) DEFAULT '0' NOT NULL,
 TERMINAL_PRICE NUMBER(10,0),
 DISCOUNT_RATE NUMBER(4,2),
 SALES_VALUE NUMBER(12,2),
 NOTES VARCHAR2(4000),
 CREATE_STAMP TIMESTAMP DEFAULT SYSTIMESTAMP NOT NULL,
 CREATE_USER_ID VARCHAR2(6) NOT NULL,
 UPDATE_STAMP TIMESTAMP,
 UPDATE_USER_ID VARCHAR2(6),
 CONSTRAINT MOBILE_PHONE_PK PRIMARY KEY (TERMINAL_ID)
);

COMMENT ON TABLE MOBILE_PHONE IS '端末情報マスタ';
COMMENT ON COLUMN MOBILE_PHONE.TERMINAL_ID IS '端末ID';
COMMENT ON COLUMN MOBILE_PHONE.TERMINAL_NAME IS '端末名';
COMMENT ON COLUMN MOBILE_PHONE.SALES_DATE IS '発売日';
COMMENT ON COLUMN MOBILE_PHONE.VENDOR_NAME IS 'ベンダー';
COMMENT ON COLUMN MOBILE_PHONE.CARRIER_TYPE IS 'キャリア';
COMMENT ON COLUMN MOBILE_PHONE.FLASH_LEVEL IS 'FLASHバージョン';
COMMENT ON COLUMN MOBILE_PHONE.GIF_FLAG IS 'GIF対応フラグ';
COMMENT ON COLUMN MOBILE_PHONE.VERSION IS 'バージョン番号';
COMMENT ON COLUMN MOBILE_PHONE.DEL_FLAG IS '論理削除フラグ';
COMMENT ON COLUMN MOBILE_PHONE.TERMINAL_PRICE IS '端末価格';
COMMENT ON COLUMN MOBILE_PHONE.DISCOUNT_RATE IS '割引率';
COMMENT ON COLUMN MOBILE_PHONE.SALES_VALUE IS '販売価格';
COMMENT ON COLUMN MOBILE_PHONE.NOTES IS '備考';
COMMENT ON COLUMN MOBILE_PHONE.CREATE_STAMP IS '作成日';
COMMENT ON COLUMN MOBILE_PHONE.CREATE_USER_ID IS '作成者';
COMMENT ON COLUMN MOBILE_PHONE.UPDATE_STAMP IS '更新日';
COMMENT ON COLUMN MOBILE_PHONE.UPDATE_USER_ID IS '更新者';


-- テーブル初期化
TRUNCATE TABLE MOBILE_PHONE;

